tables.table_schema:
CREATE TABLE RiskManagementSystem (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    exposure DECIMAL,
    portfolio_performance DECIMAL
);

CREATE TABLE MarketMicrostructureRiskManagement (
    id INT PRIMARY KEY,
    exposure DECIMAL,
    portfolio_performance DECIMAL
);

CREATE TABLE MarketParticipants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    trading_capacity VARCHAR(50)
);

CREATE TABLE Market_Microstructure_Risks (
    entity_id INT PRIMARY KEY, 
    entity_name VARCHAR(255) NOT NULL, 
    exposure DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Portfolio_Performance (
    portfolio_id INT PRIMARY KEY, 
    portfolio_name VARCHAR(255) NOT NULL, 
    total_value DECIMAL(12, 2) NOT NULL
);

CREATE TABLE RiskManagementSystem (
    id INT PRIMARY KEY,
    client_id INT,
    exposure DECIMAL,
    risk_score DECIMAL
);

CREATE TABLE PortfolioPerformance (
    id INT PRIMARY KEY,
    portfolio_id INT,
    performance DECIMAL
);

CREATE TABLE MarketMicrostructure (
    id INT PRIMARY KEY,
    security_id INT,
    market_price DECIMAL
);

CREATE TABLE ExposureAnalysis (
    id INT PRIMARY KEY,
    analysis_id INT,
    exposure_rating VARCHAR
);

CREATE TABLE exposure (
  id INT PRIMARY KEY,
  risk_level INT,
  portfolio_id INT
);

CREATE TABLE performance (
  id INT PRIMARY KEY,
  portfolio_id INT,
  returns DECIMAL(10,2),
  date DATE
);

CREATE TABLE market_data (
  id INT PRIMARY KEY,
  symbol VARCHAR(50),
  price DECIMAL(10,2),
  volume INT
);

CREATE TABLE risk_metrics (
  id INT PRIMARY KEY,
  risk_type VARCHAR(50),
  value DECIMAL(10,2)
);

CREATE TABLE portfolio (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  manager VARCHAR(50),
  strategy VARCHAR(50)
);
