tables.table_schema:
CREATE TABLE RiskParameters (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    risk_limit FLOAT,
    risk_threshold FLOAT
);

CREATE TABLE ExecutionStrategies (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    execution_type VARCHAR(50),
    execution_limit FLOAT
);

CREATE TABLE RiskLimits (
    id INT PRIMARY KEY,
    strategy_id INT,
    max_daily_loss FLOAT,
    max_position_size INT
);

CREATE TABLE RiskParameters (
    risk_parameter_id INT PRIMARY KEY,
    strategy_id INT,
    parameter_name VARCHAR(50),
    parameter_value FLOAT
);

CREATE TABLE StrategyLimits (
    limit_id INT PRIMARY KEY,
    strategy_id INT,
    max_order_size INT,
    max_position_size INT
);

CREATE TABLE ExecutionStrategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    risk_rule VARCHAR(100),
    execution_type VARCHAR(20)
);

CREATE TABLE StrategyMetrics (
    metric_id INT PRIMARY KEY,
    strategy_id INT,
    metric_name VARCHAR(50),
    metric_value FLOAT
);

CREATE TABLE risk_parameters (
    parameter_id INT PRIMARY KEY,
    parameter_name TEXT NOT NULL,
    parameter_value FLOAT NOT NULL
);

CREATE TABLE strategy_limits (
    limit_id INT PRIMARY KEY,
    limit_name TEXT NOT NULL,
    max_limit FLOAT NOT NULL,
    min_limit FLOAT NOT NULL
);

CREATE TABLE strategy_execution (
    execution_id INT PRIMARY KEY,
    strategy_name TEXT NOT NULL,
    execution_time TIMESTAMP NOT NULL
);

CREATE TABLE algorithmic_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name TEXT NOT NULL,
    strategy_description TEXT
);
