tables.table_schema:
CREATE TABLE transaction_costs (
    trade_id UUID PRIMARY KEY,
    algorithm_id UUID,
    symbol VARCHAR(50),
    cost DECIMAL
);

CREATE TABLE algorithm_data (
    algorithm_id UUID PRIMARY KEY,
    name VARCHAR(50),
    description TEXT
);

CREATE TABLE transaction_costs (
    id INT PRIMARY KEY,
    algorithm_id INT,
    transaction_date DATE,
    transaction_cost DECIMAL
);

CREATE TABLE algorithms (
    id INT PRIMARY KEY,
    algorithm_name VARCHAR,
    strategy VARCHAR,
    description TEXT
);

CREATE TABLE trades (
    id INT PRIMARY KEY,
    algorithm_id INT,
    trade_date DATE,
    trade_cost DECIMAL
);

CREATE TABLE algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    algorithm_description TEXT,
    algorithm_type VARCHAR(50)
);

CREATE TABLE trading_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    strategy_description TEXT,
    risk_tolerance VARCHAR(50)
);
