tables.table_schema:
CREATE TABLE volatility_data (
    date DATE,
    symbol TEXT,
    volatility DECIMAL
);

CREATE TABLE market_trends (
    trend_id SERIAL PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT
);

CREATE TABLE volatility_index (
    index_id SERIAL PRIMARY KEY,
    date DATE,
    volatility DECIMAL(8, 2)
);

CREATE TABLE market_symbols (
    symbol_id SERIAL PRIMARY KEY,
    symbol_name VARCHAR(10)
);

CREATE TABLE MarketParticipants (
    id SERIAL PRIMARY KEY,
    participant_name TEXT NOT NULL,
    participant_type TEXT NOT NULL,
    region TEXT NOT NULL
);

CREATE TABLE AnalysisLogs (
    id SERIAL PRIMARY KEY,
    analysis_type TEXT NOT NULL,
    analysis_date DATE NOT NULL,
    results JSONB
);
