CREATE TABLE counterparty_credit_ratings (
    counterparty_id INT PRIMARY KEY,
    credit_rating VARCHAR(50),
    date_updated DATE
);

CREATE TABLE exposure_limits (
    counterparty_id INT PRIMARY KEY,
    exposure_limit FLOAT,
    date_updated DATE
);

CREATE TABLE counterparty_details (
    counterparty_id INT PRIMARY KEY,
    counterparty_name VARCHAR(100),
    country VARCHAR(50),
    industry VARCHAR(50)
);

CREATE TABLE Counterparty (
    counterparty_id UUID PRIMARY KEY,
    counterparty_name TEXT NOT NULL
);

CREATE TABLE ExternalRatingAgency (
    agency_id UUID PRIMARY KEY,
    agency_name TEXT NOT NULL
);

CREATE TABLE credit_ratings (
    id INT PRIMARY KEY,
    counterparty_name VARCHAR(50),
    credit_rating VARCHAR(10),
    rating_date DATE
);

CREATE TABLE exposure_limits (
    id INT PRIMARY KEY,
    counterparty_name VARCHAR(50),
    limit_amount DECIMAL(10, 2),
    limit_type VARCHAR(20)
);

CREATE TABLE counterparty_details (
    id INT PRIMARY KEY,
    counterparty_name VARCHAR(50),
    address VARCHAR(100),
    contact_number VARCHAR(15)
);

CREATE TABLE risk_exposures (
    id INT PRIMARY KEY,
    counterparty_name VARCHAR(50),
    exposure_amount DECIMAL(10, 2),
    exposure_date DATE
);

CREATE TABLE credit_score_history (
    id INT PRIMARY KEY,
    counterparty_name VARCHAR(50),
    credit_score INT,
    score_date DATE
);
