tables.table_schema:
CREATE TABLE trades (trade_id INT PRIMARY KEY,
    trade_date DATE,
    security_symbol VARCHAR(50),
    quantity INT,
    trade_amount DECIMAL(10,2)
);

CREATE TABLE trade_confirmations (confirmation_id INT PRIMARY KEY,
    trade_id INT,
    confirmation_date DATE,
    confirmed_quantity INT,
    confirmed_amount DECIMAL(10,2)
);

CREATE TABLE reconciliation_logs (log_id INT PRIMARY KEY,
    trade_id INT,
    confirmation_id INT,
    reconciliation_date DATE,
    result VARCHAR(50)
);

CREATE TABLE external_trade_confirmation (
    confirmation_id INT PRIMARY KEY,
    trade_id INT,
    confirmation_date DATE,
    is_confirmed BOOLEAN
);

CREATE TABLE reconciliation_log (
    log_id INT PRIMARY KEY,
    trade_id INT,
    comparison_result VARCHAR(100),
    reconciliation_date DATE
);

CREATE TABLE trade_discrepancies (
    discrepancy_id INT PRIMARY KEY,
    log_id INT,
    discrepancy_description TEXT,
    resolution_status VARCHAR(50)
);

CREATE TABLE trade_errors (
    error_id INT PRIMARY KEY,
    trade_id INT,
    error_type VARCHAR(50),
    error_description TEXT
);

CREATE TABLE trade_logs (
    log_id INT PRIMARY KEY,
    log_date DATETIME,
    log_message TEXT
);
