tables.table_schema:
CREATE TABLE Portfolio (
    portfolio_id INT PRIMARY KEY,
    portfolio_name VARCHAR(255),
    portfolio_value DECIMAL
);

CREATE TABLE MarketStressScenario (
    scenario_id INT PRIMARY KEY,
    scenario_name VARCHAR(255),
    stress_level INT,
    date_created TIMESTAMP
);

CREATE TABLE market_stress_scenarios (
    scenario_id INT PRIMARY KEY,
    scenario_name VARCHAR,
    stress_level INT,
    stress_description TEXT
);

CREATE TABLE portfolio_simulation (
    simulation_id INT PRIMARY KEY,
    portfolio_id INT,
    date DATE,
    simulation_results TEXT
);

CREATE TABLE market_simulation (
    simulation_id INT PRIMARY KEY,
    scenario_id INT,
    date DATE,
    simulation_results TEXT
);
