tables.table_schema:
CREATE TABLE risk_management_actions (
  action_id INT PRIMARY KEY,
  action_description TEXT,
  market_event_type TEXT,
  action_date TIMESTAMP
);

CREATE TABLE market_events (
  event_id INT PRIMARY KEY,
  event_type TEXT,
  event_description TEXT,
  event_date TIMESTAMP
);

CREATE TABLE RiskManagementActions (
	risk_management_action_id UUID PRIMARY KEY,
	market_event_id UUID,
	action_description TEXT,
	action_date TIMESTAMP
);

CREATE TABLE MarketEvents (
	market_event_id UUID PRIMARY KEY,
	event_description TEXT,
	event_date TIMESTAMP
);

CREATE TABLE RiskCategories (
	risk_category_id UUID PRIMARY KEY,
	category_name TEXT,
	category_description TEXT
);

CREATE TABLE RiskAssessments (
	risk_assessment_id UUID PRIMARY KEY,
	risk_category_id UUID,
	assessment_description TEXT,
	assessment_date TIMESTAMP
);

CREATE TABLE MarketEvents (
  event_id INT PRIMARY KEY,
  event_date DATE,
  event_description VARCHAR(255),
  event_type VARCHAR(50)
);

CREATE TABLE RiskActions (
  action_id INT PRIMARY KEY,
  action_date DATE,
  action_description VARCHAR(255),
  severity_level INT
);

CREATE TABLE MarketEventLog (
  log_id INT PRIMARY KEY,
  event_id INT,
  action_id INT,
  log_date DATE
);

CREATE TABLE MarketImpact (
  impact_id INT PRIMARY KEY,
  event_id INT,
  impact_description TEXT,
  impact_value DECIMAL(10, 2)
);

CREATE TABLE RiskActionLog (
  log_id INT PRIMARY KEY,
  action_id INT,
  log_date DATE
);
