tables.table_schema:
CREATE TABLE historical_market_data (
    symbol VARCHAR(50),
    date DATE,
    close_price DECIMAL(10,2),
    volume INT
);
CREATE TABLE trading_algo_results (
    algorithm_name VARCHAR(100),
    backtest_date DATE,
    pnl DECIMAL(12,2),
    success BOOLEAN
);

CREATE TABLE trading_strategies (
  strategy_id INT,
  strategy_name VARCHAR(255),
  description TEXT
);

CREATE TABLE instrument (
  id INT PRIMARY KEY,
  name TEXT,
  symbol TEXT
);

CREATE TABLE trade_strategy (
  id INT PRIMARY KEY,
  name TEXT,
  description TEXT
);

CREATE TABLE stock_dividends (
    symbol VARCHAR(10),
    ex_dividend_date DATE,
    dividend FLOAT
);

CREATE TABLE stock_splits (
    symbol VARCHAR(10),
    split_date DATE,
    split_factor FLOAT
);

CREATE TABLE stock_metadata (
    symbol VARCHAR(10),
    company_name VARCHAR(100),
    sector VARCHAR(50),
    exchange VARCHAR(50)
);

CREATE TABLE trading_strategies (
    strategy_id INT,
    strategy_name VARCHAR(50),
    description VARCHAR(200)
);
