tables.table_schema:
CREATE TABLE PricePatterns ( 
 pattern_id INT PRIMARY KEY, 
 pattern_name VARCHAR, 
 pattern_description TEXT 
);

CREATE TABLE TradingOpportunities ( 
 opportunity_id INT PRIMARY KEY, 
 opportunity_date TIMESTAMP, 
 strategy_name VARCHAR, 
 profit DECIMAL 
);

CREATE TABLE TradingSignals ( 
 signal_id INT PRIMARY KEY, 
 signal_timestamp TIMESTAMP, 
 buy_sell VARCHAR, 
 signal_strength DECIMAL 
);

CREATE TABLE MarketTrends (
    trend_id SERIAL PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT
);
