tables.table_schema:
CREATE TABLE market_news_sentiment (
	sentiment_id INT PRIMARY KEY,
	headline TEXT NOT NULL,
	sentiment_score FLOAT NOT NULL,
	publish_date TIMESTAMP NOT NULL
);

CREATE TABLE market_news_sentiment (
    news_id INT PRIMARY KEY,
    news_title TEXT,
    sentiment_score FLOAT
);

CREATE TABLE trading_decisions (
    decision_id INT PRIMARY KEY,
    decision_description TEXT,
    execution_date DATE,
    trade_amount DECIMAL
);

CREATE TABLE market_news_sentiment (
    article_id INT PRIMARY KEY,
    article_title VARCHAR(255),
    article_content TEXT,
    sentiment_score FLOAT
);

CREATE TABLE analysis_results (
    analysis_id INT PRIMARY KEY,
    article_id INT,
    analysis_date TIMESTAMP,
    analysis_result VARCHAR(50)
);

CREATE TABLE news_articles (
    article_id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    sentiment_score FLOAT
);

CREATE TABLE trading_decisions (
    decision_id INT PRIMARY KEY,
    article_id INT,
    buy_sell VARCHAR(10),
    decision_date TIMESTAMP
);

CREATE TABLE traders (
    trader_id INT PRIMARY KEY,
    trader_name VARCHAR(50),
    trader_location VARCHAR(100)
);

CREATE TABLE market_sentiment (
    sentiment_id INT PRIMARY KEY,
    article_id INT,
    overall_sentiment VARCHAR(20),
    sentiment_date DATE
);

CREATE TABLE sources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(255),
    source_type VARCHAR(50),
    source_country VARCHAR(50)
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    user_name VARCHAR(100),
    user_email VARCHAR(255),
    user_role VARCHAR(50)
);
