tables.table_schema:
CREATE TABLE trade_strategies (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    description TEXT,
    created_date DATE
);

CREATE TABLE stock_price_changes (
    symbol VARCHAR(10),
    date DATE,
    price_change DECIMAL
);

CREATE TABLE trading_strategies (
    strategy_id INT,
    strategy_name VARCHAR(50),
    description TEXT
);

CREATE TABLE stock_prices (
  symbol VARCHAR PRIMARY KEY,
  date DATE,
  price DECIMAL
);

CREATE TABLE stock_volume (
  symbol VARCHAR PRIMARY KEY,
  date DATE,
  volume INT
);

CREATE TABLE stock_metadata (
  symbol VARCHAR PRIMARY KEY,
  company_name VARCHAR,
  sector VARCHAR
);

CREATE TABLE stock_dividends (
  symbol VARCHAR PRIMARY KEY,
  ex_date DATE,
  dividend DECIMAL
);

CREATE TABLE stock_splits (
  symbol VARCHAR PRIMARY KEY,
  split_date DATE,
  split_ratio DECIMAL
);
