tables.table_schema:
CREATE TABLE market_data_feeds (
  feed_id SERIAL PRIMARY KEY,
  feed_name VARCHAR(50) NOT NULL,
  feed_source VARCHAR(100) NOT NULL,
  last_updated TIMESTAMP
);

CREATE TABLE arbitrage_decisions (
  decision_id SERIAL PRIMARY KEY,
  feed_id INT REFERENCES market_data_feeds(feed_id),
  decision TEXT,
  decision_timestamp TIMESTAMP
);

CREATE TABLE market_price_data_feed (
    id UUID PRIMARY KEY,
    symbol TEXT NOT NULL,
    price DECIMAL NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE market_volume_data_feed (
    id UUID PRIMARY KEY,
    symbol TEXT NOT NULL,
    volume DECIMAL NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE MarketDataFeeds ( 
    id INT PRIMARY KEY, 
    symbol VARCHAR(50) NOT NULL, 
    price DECIMAL(10,2) NOT NULL, 
    timestamp TIMESTAMP NOT NULL 
);

CREATE TABLE UserSettings ( 
    id INT PRIMARY KEY, 
    user_id INT NOT NULL, 
    notification_enabled BOOLEAN NOT NULL, 
    notification_interval INT NOT NULL 
);

CREATE TABLE arbitrage_decisions (
    decision_id SERIAL PRIMARY KEY,
    opportunity_id INT NOT NULL,
    decision_timestamp TIMESTAMP NOT NULL,
    decision VARCHAR(100) NOT NULL
);

CREATE TABLE market_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    registration_date TIMESTAMP NOT NULL
);
