tables.table_schema:
CREATE TABLE trades (
 trade_id INT PRIMARY KEY,
 trade_date DATE,
 trade_account VARCHAR,
 trade_amount DECIMAL
);

CREATE TABLE reconciliations (
 reconciliation_id INT PRIMARY KEY,
 reconciliation_date DATE,
 trade_id INT,
 reconciliation_status VARCHAR
);

CREATE TABLE trade_settlements (
 trade_id UUID PRIMARY KEY,
 trade_date DATE,
 settlement_amount DECIMAL,
 broker_id UUID
);

CREATE TABLE reconciliation_records (
 record_id UUID PRIMARY KEY,
 trade_id UUID,
 record_timestamp TIMESTAMP,
 discrepancy_amount DECIMAL
);

CREATE TABLE arbitrage_trading (
 trade_id UUID PRIMARY KEY,
 buy_amount DECIMAL,
 sell_amount DECIMAL,
 profit DECIMAL
);

CREATE TABLE trade_settlement_information (
    trade_id INT PRIMARY KEY,
    trade_date DATE,
    settlement_amount DECIMAL,
    settlement_status VARCHAR(50)
);

CREATE TABLE reconciliation_records (
    record_id INT PRIMARY KEY,
    trade_id INT,
    reconciliation_date DATE,
    reconciliation_status VARCHAR(50)
);

CREATE TABLE arbitrage_trading (
    trade_id INT PRIMARY KEY,
    trade_date DATE,
    trading_amount DECIMAL,
    trading_status VARCHAR(50)
);

CREATE TABLE audit_trail (
    audit_id INT PRIMARY KEY,
    trade_id INT,
    audit_date DATE,
    audit_type VARCHAR(50)
);

CREATE TABLE counterparty_info (
    counterparty_id INT PRIMARY KEY,
    counterparty_name VARCHAR(100),
    counterparty_type VARCHAR(50)
);}
