tables.table_schema:
CREATE TABLE earnings_call_sentiment (
    company_name VARCHAR(100) NOT NULL,
    transcript_text TEXT NOT NULL,
    sentiment_score FLOAT NOT NULL,
    transcript_date DATE NOT NULL
);

CREATE TABLE earnings_transcripts (
    transcript_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    transcript_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE investor_sentiments (
    sentiment_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    sentiment_score FLOAT,
    sentiment_category VARCHAR(50)
);

CREATE TABLE company_sentiment (
    company_id INT PRIMARY KEY,
    transcript_id INT,
    sentiment_score FLOAT
);

CREATE TABLE transcripts (
    transcript_id INT PRIMARY KEY,
    transcript_text TEXT,
    transcript_date DATE
);

CREATE TABLE investors (
    investor_id INT PRIMARY KEY,
    investor_name VARCHAR(50),
    contact_info VARCHAR(100)
);

CREATE TABLE earnings_call_transcripts (
    id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    transcript_text TEXT NOT NULL,
    sentiment_score DECIMAL(5, 2) NOT NULL
);

CREATE TABLE analysts_reviews (
    id INT PRIMARY KEY,
    transcript_id INT NOT NULL,
    analyst_name VARCHAR(255) NOT NULL,
    analyst_sentiment DECIMAL(5, 2) NOT NULL
);

CREATE TABLE investor_sentiment (
    id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    investor_id INT NOT NULL,
    sentiment_category VARCHAR(50) NOT NULL
);

CREATE TABLE company_details (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100) NOT NULL,
    country VARCHAR(50) NOT NULL
);

CREATE TABLE companies (
  company_id UUID PRIMARY KEY,
  company_name TEXT NOT NULL,
  industry TEXT NOT NULL
);

CREATE TABLE investors (
  investor_id UUID PRIMARY KEY,
  investor_name TEXT NOT NULL,
  email TEXT NOT NULL
);

CREATE TABLE earnings_call_dates (
  call_id UUID PRIMARY KEY,
  transcript_id UUID NOT NULL,
  call_date DATE NOT NULL,
  company_id UUID NOT NULL
);
