tables.table_schema:
CREATE TABLE FinancialReports (
 report_id UUID PRIMARY KEY,
 report_text TEXT
 );

CREATE TABLE MarketSentiment (
 sentiment_id UUID PRIMARY KEY,
 report_id UUID REFERENCES FinancialReports(report_id),
 sentiment_score FLOAT
 );

CREATE TABLE financial_reports (
 report_id INT PRIMARY KEY,
 report_text TEXT
);

CREATE TABLE sentiment_scores (
 score_id INT PRIMARY KEY,
 report_id INT,
 sentiment_score FLOAT,
 FOREIGN KEY (report_id) REFERENCES financial_reports(report_id)
);

CREATE TABLE market_sentiment (
 market_id INT PRIMARY KEY,
 market_sentiment VARCHAR(255)
);

CREATE TABLE report_sentiment (
 report_id INT,
 market_id INT,
 sentiment_label VARCHAR(25),
 FOREIGN KEY (report_id) REFERENCES financial_reports(report_id),
 FOREIGN KEY (market_id) REFERENCES market_sentiment(market_id)
);
