tables.table_schema:
CREATE TABLE forum_sentiments (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    message_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE forum_posts (
    post_id INT PRIMARY KEY,
    author VARCHAR(50),
    message TEXT,
    post_date TIMESTAMP
);

CREATE TABLE chatroom_messages (
    message_id INT PRIMARY KEY,
    sender VARCHAR(50),
    message TEXT,
    message_date TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    post_id INT REFERENCES forum_posts(post_id),
    message_id INT REFERENCES chatroom_messages(message_id),
    sentiment_score FLOAT
);

CREATE TABLE forum_messages (
    message_id UUID PRIMARY KEY,
    message_text TEXT,
    author_id UUID,
    sentiment_score FLOAT
);

CREATE TABLE chatroom_messages (
    message_id UUID PRIMARY KEY,
    message_text TEXT,
    author_id UUID,
    sentiment_score FLOAT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE market_trends (
    trend_id UUID PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    trend_date TIMESTAMP
);

CREATE TABLE ForumMessages (
    message_id INT PRIMARY KEY,
    message_text TEXT,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE ChatMessages (
    message_id INT PRIMARY KEY,
    message_text TEXT,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    join_date DATE
);

CREATE TABLE Forums (
    forum_id INT PRIMARY KEY,
    forum_name VARCHAR(50),
    category VARCHAR(50)
);

CREATE TABLE Chatrooms (
    chatroom_id INT PRIMARY KEY,
    chatroom_name VARCHAR(50),
    capacity INT
);
