tables.table_schema:
CREATE TABLE earnings_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    company_name VARCHAR(255),
    report_text TEXT
);

CREATE TABLE stock_predictions (
    prediction_id INT PRIMARY KEY,
    report_id INT,
    stock_price_change DECIMAL(10,2),
    prediction_result VARCHAR(50)
    FOREIGN KEY (report_id) REFERENCES earnings_reports(report_id)
);

CREATE TABLE earnings_report (
  report_id INT PRIMARY KEY,
  report_date DATE,
  company_name VARCHAR(50),
  sentiment_score FLOAT
);

CREATE TABLE stock_price (
  stock_id INT PRIMARY KEY,
  stock_symbol VARCHAR(10),
  price FLOAT,
  volume INT
);

CREATE TABLE prediction (
  prediction_id INT PRIMARY KEY,
  report_id INT,
  stock_id INT,
  predicted_movement VARCHAR(10)
);

CREATE TABLE earnings_reports (
    report_id UUID PRIMARY KEY,
    company_name TEXT,
    report_date DATE,
    report_text TEXT
);

CREATE TABLE companies (
    company_id UUID PRIMARY KEY,
    company_name TEXT,
    industry TEXT
);
