tables.table_schema:
CREATE TABLE neural_network_training_data (
    id SERIAL PRIMARY KEY,
    input_data JSONB NOT NULL,
    output_data JSONB NOT NULL
);

CREATE TABLE stocks (
    stock_id INT,
    stock_name VARCHAR,
    sector VARCHAR
);

CREATE TABLE stock_statistics (
    stock_symbol VARCHAR(10),
    date DATE,
    average_price DECIMAL,
    total_volume INT
);

CREATE TABLE neural_network_layers (
    layer_id INT,
    layer_type VARCHAR(20),
    activation_function VARCHAR(20)
);

CREATE TABLE training_data (
    stock_symbol VARCHAR(10),
    input_features JSON,
    target_output JSON
);

CREATE TABLE stock_symbols (
    symbol_id INT PRIMARY KEY,
    symbol_name VARCHAR(50) NOT NULL
);
