tables.table_schema:
CREATE TABLE RiskAssessmentData (
    id INT PRIMARY KEY,
    stock_symbol TEXT NOT NULL,
    risk_score FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE risk_assessment_data (
    id UUID PRIMARY KEY,
    date DATE,
    stock_symbol VARCHAR,
    risk_score FLOAT
);

CREATE TABLE trading_strategies (
    strategy_id UUID PRIMARY KEY,
    strategy_name VARCHAR,
    created_date DATE,
    risk_level FLOAT
);

CREATE TABLE neural_networks (
    neural_id UUID PRIMARY KEY,
    neural_name VARCHAR,
    layers INT,
    activation_function VARCHAR
);

CREATE TABLE trade_history (
    history_id UUID PRIMARY KEY,
    trade_id UUID,
    executed_date DATE,
    performance FLOAT
);
