tables.table_schema:
CREATE TABLE news_sentiment (
	headline TEXT,
	sentiment_score FLOAT
);

CREATE TABLE news_headlines (
    id SERIAL PRIMARY KEY,
    headline TEXT,
    sentiment_score FLOAT
);

CREATE TABLE sentiment_analysis (
    id SERIAL PRIMARY KEY,
    headline_id INT,
    analysis_type TEXT,
    sentiment TEXT
);

CREATE TABLE news_headlines (
    headline_id INT PRIMARY KEY,
    headline TEXT NOT NULL,
    sentiment_score FLOAT NOT NULL
);

CREATE TABLE sentiment_scores (
    score_id INT PRIMARY KEY,
    score FLOAT NOT NULL,
    news_id INT NOT NULL,
    FOREIGN KEY (news_id) REFERENCES news_headlines(headline_id)
);

CREATE TABLE trading_neural_networks (
    network_id INT PRIMARY KEY,
    network_name TEXT NOT NULL,
    last_trained TIMESTAMP NOT NULL
);

CREATE TABLE news_headlines (
    headline_id INT PRIMARY KEY,
    headline_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE trading_networks (
    network_id INT PRIMARY KEY,
    network_name TEXT,
    network_architecture TEXT,
    performance_score FLOAT
);

CREATE TABLE analysts (
    analyst_id INT PRIMARY KEY,
    analyst_name TEXT,
    expertise_area TEXT
);

CREATE TABLE trades (
    trade_id INT PRIMARY KEY,
    trade_type TEXT,
    trade_quantity INT,
    trade_price FLOAT
);

CREATE TABLE trade_analysis (
    analysis_id INT PRIMARY KEY,
    trade_id INT,
    analyst_id INT,
    sentiment_score FLOAT
);
