tables.table_schema:
CREATE TABLE SocialMediaPosts (
    post_id INT PRIMARY KEY,
    post_text TEXT,
    post_date DATE,
    sentiment_score FLOAT
);

CREATE TABLE TradingDecisions (
    decision_id INT PRIMARY KEY,
    buy_sell VARCHAR(5),
    trade_date DATE,
    decision_reason TEXT
);

CREATE TABLE sentiment_analysis (
    id SERIAL PRIMARY KEY,
    post_id INT,
    sentiment_score FLOAT,
    sentiment_label VARCHAR(10)
);

CREATE TABLE trading_decisions (
    id SERIAL PRIMARY KEY,
    post_id INT,
    buy_or_sell VARCHAR(4),
    confidence_score FLOAT
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    followers_count INT,
    join_date DATE
);

CREATE TABLE sentiment_analysis_records (
    record_id INT PRIMARY KEY,
    post_id INT,
    sentiment_category VARCHAR(20),
    sentiment_strength FLOAT
);

CREATE TABLE trading_decisions (
    decision_id INT PRIMARY KEY,
    post_id INT,
    buy_sell_action VARCHAR(10),
    confidence_score FLOAT
);
