tables.table_schema:
CREATE TABLE climate_projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50),
    project_description TEXT
);

CREATE TABLE simulation_results (
    result_id INT PRIMARY KEY,
    project_id INT,
    simulation_date DATE,
    result_data JSON
);

CREATE TABLE ClimateModels (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    simulation_date DATE,
    simulation_results JSON
);

CREATE TABLE ClimateModels (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50),
    model_description TEXT
);

CREATE TABLE SimulationRuns (
    run_id INT PRIMARY KEY,
    model_id INT REFERENCES ClimateModels(model_id),
    start_date DATE,
    end_date DATE
);

CREATE TABLE SimulationResults (
    result_id INT PRIMARY KEY,
    run_id INT REFERENCES SimulationRuns(run_id),
    result_date DATE,
    result_value FLOAT
);

CREATE TABLE ResearchProjects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50),
    project_description TEXT
);

CREATE TABLE climate_models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(50),
  model_type VARCHAR(50),
  model_description TEXT
);

CREATE TABLE research_teams (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  team_lead VARCHAR(50),
  team_size INT
);

CREATE TABLE publications (
  publication_id INT PRIMARY KEY,
  result_id INT REFERENCES experiment_results(result_id),
  publication_title VARCHAR(100),
  publication_date DATE
);
