tables.table_schema:
CREATE TABLE satellite_data (
    id SERIAL PRIMARY KEY,
    satellite_id INT,
    data_point TIMESTAMP,
    temperature FLOAT
);

CREATE TABLE satellite_images (
    id SERIAL PRIMARY KEY,
    image_id INT,
    image_timestamp TIMESTAMP,
    image_url TEXT
);

CREATE TABLE Satellite_Metadata (
    satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR,
    launch_date DATE,
    status VARCHAR
);

CREATE TABLE Satellite_Images (
    image_id INT PRIMARY KEY,
    satellite_id INT,
    image_date TIMESTAMP,
    image_data BYTEA
);

CREATE TABLE Environmental_Data (
    data_id INT PRIMARY KEY,
    location VARCHAR,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE Climate_Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR,
    event_type VARCHAR,
    date_start DATE,
    date_end DATE
);

CREATE TABLE Satellite_Commands (
    command_id INT PRIMARY KEY,
    satellite_id INT,
    command_type VARCHAR,
    command_date TIMESTAMP
);
