tables.table_schema:
CREATE TABLE city_emissions (
    city_id INT,
    emission_category VARCHAR(255),
    emission_value DECIMAL,
    year INT
);

CREATE TABLE emission_goals (
    goal_id INT,
    goal_description VARCHAR(255),
    goal_year INT
);

CREATE TABLE emissions (
   id INT PRIMARY KEY,
   year INT,
   emissions_metric DECIMAL
);

CREATE TABLE target_emissions (
   id INT PRIMARY KEY,
   year INT,
   target_metric DECIMAL
);

CREATE TABLE action_items (
   id INT PRIMARY KEY,
   action_description TEXT,
   completion_date DATE,
   status VARCHAR
);

CREATE TABLE city_officials (
   id INT PRIMARY KEY,
   name VARCHAR,
   title VARCHAR
);
