    Area_ID INT PRIMARY KEY,
    Location VARCHAR(255),
    Deforestation_Rate DECIMAL(10,2),
    Year DATE
);

CREATE TABLE deforestation_data (
   country VARCHAR(50),
   region VARCHAR(50),
   deforestation_rate FLOAT,
   year INT
);

CREATE TABLE tree_species (
   species_id INT PRIMARY KEY,
   species_name VARCHAR(100),
   endangered BOOLEAN,
   habitat VARCHAR(100)
);

CREATE TABLE deforestation_rates ( 
   id INT PRIMARY KEY, 
   region VARCHAR(255), 
   year INT, 
   deforestation_rate FLOAT 
 );

CREATE TABLE forest_owners ( 
   owner_id INT PRIMARY KEY, 
   owner_name VARCHAR(255), 
   location VARCHAR(255) 
 );

CREATE TABLE protection_measures ( 
   measure_id INT PRIMARY KEY, 
   measure_name VARCHAR(255), 
   description TEXT 
 );

CREATE TABLE deforestation_data (
    location VARCHAR(255),
    deforestation_rate FLOAT,
