tables.table_schema:
CREATE TABLE soil_moisture_data (
   id INT PRIMARY KEY,
   location VARCHAR(100),
   date_collected DATE,
   moisture_level DECIMAL
);

CREATE TABLE soil_moisture_data (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    reading_date DATE,
    moisture_level DECIMAL
);

CREATE TABLE farm_fields (
    id INT PRIMARY KEY,
    field_name VARCHAR(255),
    field_area DECIMAL,
    crop_type VARCHAR(255)
);

CREATE TABLE SoilMoistureData (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  location VARCHAR(50) NOT NULL,
  moisture_level DECIMAL NOT NULL
);

CREATE TABLE FarmFields (
  field_id SERIAL PRIMARY KEY,
  field_name VARCHAR(50) NOT NULL,
  field_location VARCHAR(100) NOT NULL
);

CREATE TABLE Sensors (
  sensor_id SERIAL PRIMARY KEY,
  sensor_type VARCHAR(50) NOT NULL,
  field_id INT NOT NULL,
  FOREIGN KEY (field_id) REFERENCES FarmFields(field_id)
);

CREATE TABLE Researchers (
  researcher_id SERIAL PRIMARY KEY,
  researcher_name VARCHAR(100) NOT NULL,
  email VARCHAR(100) NOT NULL
);
