tables.table_schema:
CREATE TABLE WeatherData (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE historical_weather_data (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE city_weather_data (
    city_id INT,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE climate_trends (
    year INT,
    trend_description TEXT
);

CREATE TABLE weather_data (
  id INT PRIMARY KEY,
  date DATE,
  temperature INT,
  humidity INT
);

CREATE TABLE location_data (
  location_id INT PRIMARY KEY,
  city VARCHAR,
  country VARCHAR
);

CREATE TABLE climate_data (
  climate_id INT PRIMARY KEY,
  weather_condition VARCHAR,
  wind_speed INT
);

CREATE TABLE analysis_data (
  analysis_id INT PRIMARY KEY,
  trend VARCHAR,
  factor VARCHAR
);
