tables.table_schema:
CREATE TABLE sea_level_rise_data (
  location_id INT PRIMARY KEY,
  sea_level_height DECIMAL(10, 2),
  measurement_date DATE
);

CREATE TABLE coastal_erosion_data (
  location_id INT PRIMARY KEY,
  erosion_rate DECIMAL(10, 2),
  erosion_area VARCHAR(50),
  measurement_date DATE
);

CREATE TABLE SeaLevelData (
    MeasurementID SERIAL PRIMARY KEY,
    Location VARCHAR(100) NOT NULL,
    MeasurementDate DATE NOT NULL,
    SeaLevel FLOAT NOT NULL
);

CREATE TABLE CoastalErosionData (
    ErosionID SERIAL PRIMARY KEY,
    Location VARCHAR(100) NOT NULL,
    MeasurementDate DATE NOT NULL,
    ErosionRate FLOAT NOT NULL
);

CREATE TABLE AdaptationPlan (
    PlanID SERIAL PRIMARY KEY,
    PlanDescription TEXT,
    StartDate DATE NOT NULL,
    EndDate DATE NOT NULL
);

CREATE TABLE Sea_Level_Rise_Data (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  sea_level_height FLOAT,
  measurement_date DATE
);

CREATE TABLE Coastal_Erosion_Data (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  erosion_rate FLOAT,
  measurement_date DATE
);

CREATE TABLE Adaptation_Planning (
  id INT PRIMARY KEY,
  strategy VARCHAR(255),
  recommended_action TEXT,
  implementation_date DATE
);

CREATE TABLE Monitoring_Station (
  id INT PRIMARY KEY,
  station_name VARCHAR(255),
  location VARCHAR(255),
  installation_date DATE
);
