tables.table_schema:
CREATE TABLE project_timeline (
	project_id INT PRIMARY KEY,
	project_name VARCHAR(255),
	start_date DATE,
	end_date DATE
);

CREATE TABLE project_timelines (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE milestones (
    milestone_id SERIAL PRIMARY KEY,
    milestone_name VARCHAR(255) NOT NULL,
    due_date DATE NOT NULL,
    project_id INT REFERENCES project_timelines(project_id)
);

CREATE TABLE timelines (
    timeline_id INT PRIMARY KEY,
    timeline_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE milestones (
    milestone_id INT PRIMARY KEY,
    milestone_name VARCHAR(255),
    milestone_description TEXT,
    timeline_id INT REFERENCES timelines(timeline_id)
);

CREATE TABLE resources (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(255),
    resource_type VARCHAR(50)
);

CREATE TABLE allocation (
    allocation_id INT PRIMARY KEY,
    milestone_id INT REFERENCES milestones(milestone_id),
    resource_id INT REFERENCES resources(resource_id),
    allocation_percentage FLOAT
);

CREATE TABLE project (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE milestone (
    milestone_id SERIAL PRIMARY KEY,
    milestone_name VARCHAR(255) NOT NULL,
    project_id INT NOT NULL,
    milestone_date DATE NOT NULL,
    description TEXT
);

CREATE TABLE timeline (
    timeline_id SERIAL PRIMARY KEY,
    project_id INT NOT NULL,
    milestone_id INT NOT NULL,
    start_date TIMESTAMP NOT NULL,
    end_date TIMESTAMP NOT NULL
);

CREATE TABLE resource (
    resource_id SERIAL PRIMARY KEY,
    resource_name VARCHAR(255) NOT NULL,
    resource_type VARCHAR(50) NOT NULL
);

CREATE TABLE project_resource (
    project_id INT NOT NULL,
    resource_id INT NOT NULL,
    PRIMARY KEY (project_id, resource_id)
);
