tables.table_schema:
CREATE TABLE safety_compliance_data (
    id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    department VARCHAR(50),
    compliance_status BOOLEAN
);

CREATE TABLE incident_reports (
    report_id INT PRIMARY KEY,
    incident_description TEXT,
    report_date DATE,
    reported_by VARCHAR(50)
);

CREATE TABLE Safety_Team (
  team_id UUID PRIMARY KEY,
  team_name VARCHAR NOT NULL,
  team_lead VARCHAR,
  team_size INT
);

CREATE TABLE Compliance_Regulations (
  regulation_id SERIAL PRIMARY KEY,
  regulation_name VARCHAR NOT NULL,
  regulation_description TEXT
);

CREATE TABLE Employee (
  employee_id UUID PRIMARY KEY,
  employee_name VARCHAR NOT NULL,
  employee_role VARCHAR,
  employee_department VARCHAR
);

CREATE TABLE Incident_Reports (
  report_id SERIAL PRIMARY KEY,
  date_reported DATE NOT NULL,
  description TEXT,
  employee_id UUID
);

CREATE TABLE Safety_Checklist (
  checklist_id SERIAL PRIMARY KEY,
  checklist_name VARCHAR NOT NULL,
  checklist_items JSONB
);
