tables.table_schema:
CREATE TABLE traffic_flow_data (
    location_id INT,
    time_stamp TIMESTAMP,
    vehicle_count INT,
    avg_speed FLOAT
);

CREATE TABLE traffic_signals (
   id INT PRIMARY KEY,
   signal_name VARCHAR(50) NOT NULL,
   location VARCHAR(100) NOT NULL,
   status VARCHAR(10) NOT NULL
);

CREATE TABLE traffic_flow_data (
   flow_id INT PRIMARY KEY,
   signal_id INT NOT NULL,
   flow_time TIMESTAMP NOT NULL,
   traffic_volume INT NOT NULL,
   vehicle_speed INT NOT NULL
);
