tables.table_schema:
CREATE TABLE historical_transportation_data (
    route_id INT PRIMARY KEY,
    start_location VARCHAR(255),
    end_location VARCHAR(255),
    distance FLOAT
);

CREATE TABLE transportation_routes (
    route_id INT PRIMARY KEY,
    vehicle_type VARCHAR(50),
    speed_limit INT,
    capacity INT
);

CREATE TABLE transportation_schedules (
    schedule_id INT PRIMARY KEY,
    route_id INT REFERENCES historical_transportation_data(route_id),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE passenger_data (
    passenger_id INT PRIMARY KEY,
    name VARCHAR(100),
    age INT,
    nationality VARCHAR(50)
);
