tables.table_schema:
CREATE TABLE ParkingPermitInfo (
    permit_id INT PRIMARY KEY,
    vehicle_id INT,
    permit_type VARCHAR(50),
    issue_date DATE
);

CREATE TABLE OccupancyRates (
    date DATE PRIMARY KEY,
    occupancy_percentage DECIMAL(5,2)
);

CREATE TABLE parking_permit_information (
  permit_id SERIAL PRIMARY KEY,
  vehicle_owner_name VARCHAR(255),
  vehicle_license_plate VARCHAR(20) UNIQUE,
  permit_expiry_date DATE
);

CREATE TABLE parking_occupancy (
  parking_space_id SERIAL PRIMARY KEY,
  occupancy_status ENUM('Occupied', 'Available'),
  last_updated TIMESTAMP
);

CREATE TABLE parking_location (
  location_id SERIAL PRIMARY KEY,
  location_name VARCHAR(255) UNIQUE,
  total_spaces INTEGER,
  occupied_spaces INTEGER
);
