tables.table_schema:
CREATE TABLE transportation_infrastructure (
    infra_id INT PRIMARY KEY,
    infra_name VARCHAR(255) NOT NULL,
    infra_type VARCHAR(50) NOT NULL,
    location POINT NOT NULL
);

CREATE TABLE transportation_infrastructure (
  id INT PRIMARY KEY,
  road_name VARCHAR(255),
  road_type VARCHAR(50),
  road_length FLOAT
);

CREATE TABLE bridges (
  bridge_id INT PRIMARY KEY,
  bridge_name VARCHAR(255),
  bridge_type VARCHAR(50),
  year_built INT
);

CREATE TABLE traffic_signals (
  signal_id INT PRIMARY KEY,
  location VARCHAR(255),
  signal_type VARCHAR(50),
  status VARCHAR(20)
);

CREATE TABLE transportation_networks (
  network_id INT PRIMARY KEY,
  network_name VARCHAR(255) NOT NULL,
  network_type VARCHAR(50) NOT NULL,
  geographic_region VARCHAR(100) NOT NULL
);

CREATE TABLE roads (
  road_id INT PRIMARY KEY,
  road_name VARCHAR(255) NOT NULL,
  length_km DECIMAL(10,2),
  road_type VARCHAR(50) NOT NULL
);
