CREATE TABLE TransportationModes (
    mode_id INT,
    mode_name VARCHAR(255),
    description TEXT
);

CREATE TABLE FuelConsumption (
  vehicle_id INT,
  fuel_type VARCHAR(255),
  fuel_amount DECIMAL(10,2),
  consumption_date DATE
);

CREATE TABLE Vehicles (
  vehicle_id INT,
  vehicle_type VARCHAR(255),
  model_year INT,
  manufacturer VARCHAR(255)
);

CREATE TABLE TransportationModes (
  mode_id INT,
  mode_name VARCHAR(255)
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_type VARCHAR(255),
  fuel_type VARCHAR(255),
  fuel_capacity DECIMAL
);
