tables.table_schema:
CREATE TABLE fare_collection (
  id SERIAL PRIMARY KEY,
  rider_id INT NOT NULL,
  fare_amount DECIMAL,
  collection_date DATE
);

CREATE TABLE FareCollection (
    fare_id INT PRIMARY KEY,
    rider_id INT,
    payment_amount DECIMAL,
    payment_date TIMESTAMP
);

CREATE TABLE FinancialAnalysis (
    analysis_id INT PRIMARY KEY,
    analysis_date DATE,
    total_fare_collection DECIMAL,
    total_ridership INT
);

CREATE TABLE TransportRoutes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(255),
    start_location VARCHAR(255),
    end_location VARCHAR(255)
);

CREATE TABLE FareStatistics (
    fare_stat_id INT PRIMARY KEY,
    fare_collection_month VARCHAR(10),
    total_fare_collected DECIMAL,
    average_fare_per_ride DECIMAL
);
