tables.table_schema:
CREATE TABLE stakeholders (id INT PRIMARY KEY,
name VARCHAR(50) NOT NULL,
email VARCHAR(100) NOT NULL,
phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE partners (id INT PRIMARY KEY,
name VARCHAR(50) NOT NULL,
email VARCHAR(100) NOT NULL,
company_name VARCHAR(100) NOT NULL
);

CREATE TABLE communications (
    id INT PRIMARY KEY,
    sender_id INT,
    receiver_id INT,
    message TEXT
);

CREATE TABLE meetings (
    id INT PRIMARY KEY,
    title VARCHAR(100),
    date DATE,
    location VARCHAR(100)
);

CREATE TABLE notifications (
    id INT PRIMARY KEY,
    recipient_id INT,
    message TEXT
);
