tables.table_schema:
CREATE TABLE crimes (
  crime_id INT PRIMARY KEY,
  crime_type TEXT,
  crime_location TEXT,
  crime_date DATE
);

CREATE TABLE crime_reports (
  report_id INT PRIMARY KEY,
  crime_id INT REFERENCES crimes(crime_id),
  report_date DATE,
  report_description TEXT
);

CREATE TABLE crime_data (
    crime_id INT,
    crime_type VARCHAR(255),
    location_id INT,
    crime_date DATE
);

CREATE TABLE crime_location (
    location_id INT,
    location_name VARCHAR(255),
    city VARCHAR(255),
    state VARCHAR(255)
);

CREATE TABLE crime_reports (
    report_id INT,
    crime_id INT,
    report_date DATE,
    reporter_name VARCHAR(255)
);

CREATE TABLE crime_statistics (
    statistic_id INT,
    crime_id INT,
    report_id INT,
    crime_rating INT
);
