tables.table_schema:
CREATE TABLE environmental_areas (
    area_id UUID PRIMARY KEY,
    area_name TEXT NOT NULL,
    area_location GEOGRAPHY(POINT) NOT NULL,
    area_description TEXT
);

CREATE TABLE environmental_areas (
   area_id INT PRIMARY KEY,
   area_name VARCHAR(50) NOT NULL,
   latitude DECIMAL(10, 8) NOT NULL,
   longitude DECIMAL(11, 8) NOT NULL
);

CREATE TABLE protected_species (
   species_id INT PRIMARY KEY,
   species_name VARCHAR(50) NOT NULL,
   habitat_type VARCHAR(50),
   area_id INT REFERENCES environmental_areas(area_id)
);

CREATE TABLE environmental_areas (
    id INT PRIMARY KEY,
    area_name VARCHAR(255) NOT NULL,
    coordinates GEOGRAPHY,
    protection_level INT
);

CREATE TABLE endangered_species (
    id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL,
    population_size INT,
    habitat_id INT REFERENCES environmental_areas(id)
);

CREATE TABLE environmental_reports (
    id INT PRIMARY KEY,
    report_date DATE,
    report_details TEXT,
    author_name VARCHAR(255) NOT NULL
);
