tables.table_schema:
CREATE TABLE ClimateData (
  date DATE,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE EcosystemData (
  ecosystem_id INT,
  ecosystem_name VARCHAR(50),
  temperature_tolerance FLOAT
);

CREATE TABLE climate_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE ecosystem_data (
    id INT PRIMARY KEY,
    species_name VARCHAR,
    population_size INT,
    habitat_type VARCHAR
);

CREATE TABLE analysis_results (
    id INT PRIMARY KEY,
    analysis_date DATE,
    analysis_type VARCHAR,
    result_description TEXT
);

CREATE TABLE ecosystem_impact (
  id UUID PRIMARY KEY,
  location VARCHAR(255),
  impact_description TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE research_team (
  id UUID PRIMARY KEY,
  team_name VARCHAR(255),
  members JSONB,
  research_topic VARCHAR(255)
);

CREATE TABLE data_analysis (
  id UUID PRIMARY KEY,
  analysis_type VARCHAR(255),
  results JSONB,
  analysis_timestamp TIMESTAMP
);
