tables.table_schema:
CREATE TABLE ForestRegions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(50) NOT NULL,
  area_in_hectares DECIMAL(10, 2) NOT NULL
);

CREATE TABLE ForestRegions (
	region_id INT PRIMARY KEY,
	region_name VARCHAR(50) NOT NULL,
	area_covered FLOAT NOT NULL
);

CREATE TABLE DeforestationData (
	data_id INT PRIMARY KEY,
	region_id INT REFERENCES ForestRegions(region_id),
	year INT NOT NULL,
	deforestation_rate FLOAT NOT NULL
);

CREATE TABLE ReforestationEfforts (
	effort_id INT PRIMARY KEY,
	region_id INT REFERENCES ForestRegions(region_id),
	year INT NOT NULL,
	reforestation_area FLOAT NOT NULL
);

CREATE TABLE Organizations (
	org_id INT PRIMARY KEY,
	org_name VARCHAR(50) NOT NULL,
	org_type VARCHAR(50) NOT NULL
);
