tables.table_schema:
CREATE TABLE waste_collection (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  collection_date DATE
);

CREATE TABLE recycling_center (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  accepts_materials VARCHAR(100)
);

CREATE TABLE recycling_centers (id INT NOT NULL PRIMARY KEY,
 location VARCHAR(255) NOT NULL,
 accepted_materials VARCHAR(255) NOT NULL);
