tables.table_schema:
CREATE TABLE carbon_footprint_data (
   id SERIAL PRIMARY KEY,
   date DATE NOT NULL,
   location VARCHAR(50) NOT NULL,
   carbon_emissions DECIMAL NOT NULL
);

CREATE TABLE emission_reduction (
   id SERIAL PRIMARY KEY,
   date DATE NOT NULL,
   method VARCHAR(50) NOT NULL,
   emission_saved DECIMAL NOT NULL
);

CREATE TABLE carbon_footprint_data (
    id UUID PRIMARY KEY,
    item_name STRING,
    carbon_emissions FLOAT
);

CREATE TABLE emission_sources (
    id UUID PRIMARY KEY,
    source_name STRING,
    location STRING,
    carbon_intensity FLOAT
);

CREATE TABLE emission_reports (
    id UUID PRIMARY KEY,
    report_date DATE,
    emissions_total FLOAT,
    company_name STRING
);

CREATE TABLE carbon_footprint (
  id INT PRIMARY KEY,
  date DATE,
  category VARCHAR(50),
  value DOUBLE
);

CREATE TABLE emissions_sources (
  id INT PRIMARY KEY,
  source_name VARCHAR(100),
  emission_factor DOUBLE
);

CREATE TABLE emission_reduction (
  id INT PRIMARY KEY,
  date DATE,
  strategy VARCHAR(100),
  reduction_amount DOUBLE
);
