tables.table_schema:
CREATE TABLE air_quality_data (
  id INT PRIMARY KEY,
  location VARCHAR,
  pollution_level INT,
  timestamp TIMESTAMP
);

CREATE TABLE sensor_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR,
  location VARCHAR,
  operational_status BOOLEAN
);

CREATE TABLE PollutionEffect (
    effect_id INT PRIMARY KEY,
    effect_name VARCHAR,
    description TEXT,
    health_impact VARCHAR
);

CREATE TABLE air_quality_station (
 station_id INT PRIMARY KEY,
 location VARCHAR(255),
 sensor_id INT
);

CREATE TABLE health_records (
 record_id INT PRIMARY KEY,
 user_id INT,
 timestamp TIMESTAMP,
 health_condition VARCHAR(255)
);

CREATE TABLE pollution_sources (
 source_id INT PRIMARY KEY,
 source_name VARCHAR(255),
 location VARCHAR(255)
);

CREATE TABLE city_data (
 city_id INT PRIMARY KEY,
 city_name VARCHAR(255),
 population INT,
 pollution_level DECIMAL
);
