tables.table_schema:
CREATE TABLE LandParcelOwners (
    parcel_id UUID PRIMARY KEY,
    owner_name VARCHAR(255),
    owner_address TEXT,
    parcel_size FLOAT
);

CREATE TABLE PropertyAssessments (
    assessment_id UUID PRIMARY KEY,
    parcel_id UUID REFERENCES LandParcelOwners(parcel_id),
    assessed_value DECIMAL,
    assessment_date DATE
);

CREATE TABLE LandParcel (id INT PRIMARY KEY,
parcel_number VARCHAR(255) NOT NULL,
owner_name VARCHAR(255) NOT NULL,
owner_address VARCHAR(255) NOT NULL
);

CREATE TABLE PropertyTaxAssessment (id INT PRIMARY KEY,
parcel_id INT NOT NULL,
assessed_value DECIMAL(10, 2) NOT NULL,
assessment_date DATE NOT NULL,
FOREIGN KEY (parcel_id) REFERENCES LandParcel(id)
);

CREATE TABLE PlanningDecisions (id INT PRIMARY KEY,
parcel_id INT NOT NULL,
decision_description TEXT NOT NULL,
decision_date DATE NOT NULL,
FOREIGN KEY (parcel_id) REFERENCES LandParcel(id)
);

CREATE TABLE Taxes (
    tax_id INT PRIMARY KEY,
    property_id INT,
    tax_amount DECIMAL(10, 2),
    tax_year INT
);

CREATE TABLE Assessments (
    assessment_id INT PRIMARY KEY,
    property_id INT,
    assessment_date DATE,
    assessment_value DECIMAL(10, 2)
);

CREATE TABLE PlanningDecisions (
    decision_id INT PRIMARY KEY,
    property_id INT,
    decision_date DATE,
    decision_details TEXT
);
