tables.table_schema:
CREATE TABLE land_use_changes (
  id INT PRIMARY KEY,
  location_id INT,
  land_type VARCHAR,
  change_date DATE
);

CREATE TABLE zoning_regulations (
  id INT PRIMARY KEY,
  zoning_area_id INT,
  regulation_type VARCHAR,
  effectiveness_score DECIMAL
);

CREATE TABLE land_use_changes (id INT PRIMARY KEY, region VARCHAR(255), date DATE, land_use_type VARCHAR(255), land_area FLOAT); 

CREATE TABLE zoning_regulations (id INT PRIMARY KEY, region VARCHAR(255), zoning_type VARCHAR(255), regulations_description TEXT); 

CREATE TABLE policies (id INT PRIMARY KEY, policy_name VARCHAR(255), policy_type VARCHAR(255), implementation_date DATE, impact_assessment TEXT); 

CREATE TABLE assessments (id INT PRIMARY KEY, assessment_name VARCHAR(255), assessment_date DATE, assessment_results TEXT);

CREATE TABLE land_parcels (
  parcel_id INT PRIMARY KEY,
  parcel_area FLOAT,
  zoning_category VARCHAR(50),
  development_status VARCHAR(50)
);

CREATE TABLE zoning_regulations (
  regulation_id INT PRIMARY KEY,
  zoning_category VARCHAR(50),
  allowed_land_use VARCHAR(50),
  restriction_level VARCHAR(50)
);

CREATE TABLE land_use_history (
  record_id INT PRIMARY KEY,
  parcel_id INT,
  land_use VARCHAR(50),
  date DATE
);

CREATE TABLE policy_changes (
  change_id INT PRIMARY KEY,
  policy_name VARCHAR(50),
  change_description TEXT,
  date DATE
);

CREATE TABLE evaluation_results (
  evaluation_id INT PRIMARY KEY,
  evaluation_name VARCHAR(50),
  effectiveness_rating FLOAT,
  date DATE
);
