tables.table_schema:
CREATE TABLE EnvironmentalAssessment (
   assessment_id INT PRIMARY KEY,
   assessment_date DATE,
   environmental_impact VARCHAR(255),
   land_use_data VARCHAR(255)
);

CREATE TABLE SustainableDevelopment (
   development_id INT PRIMARY KEY,
   development_name VARCHAR(255),
   development_location VARCHAR(255),
   development_goal TEXT
);

CREATE TABLE IntegrationData (
   integration_id INT PRIMARY KEY,
   assessment_id INT,
   development_id INT
);
