tables.table_schema:
CREATE TABLE land_use_regulations (
    regulation_id UUID PRIMARY KEY,
    regulation_name TEXT,
    regulation_description TEXT
);

CREATE TABLE LandUseRegulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(50) NOT NULL,
    regulation_description TEXT,
    regulation_category VARCHAR(50) NOT NULL
);

CREATE TABLE compliance_regulations (
    regulation_id UUID PRIMARY KEY,
    regulation_name TEXT,
    regulation_description TEXT
);

CREATE TABLE land_owners (
    owner_id UUID PRIMARY KEY,
    owner_name TEXT,
    owner_address TEXT
);

CREATE TABLE BuildingPermits (
  permit_id INT PRIMARY KEY,
  permit_type VARCHAR(50) NOT NULL,
  permit_status VARCHAR(20) NOT NULL
);

CREATE TABLE LandUseRegulations (
  regulation_id INT PRIMARY KEY,
  regulation_description TEXT NOT NULL,
  regulation_category VARCHAR(50) NOT NULL
);

CREATE TABLE Buildings (
  building_id INT PRIMARY KEY,
  building_address VARCHAR(100) NOT NULL,
  building_area FLOAT NOT NULL
);

CREATE TABLE PermitApplications (
  application_id INT PRIMARY KEY,
  applicant_name VARCHAR(100) NOT NULL,
  application_date DATE NOT NULL
);

CREATE TABLE ApprovalHistory (
  approval_id INT PRIMARY KEY,
  permit_id INT NOT NULL,
  approval_date DATE NOT NULL
);
