tables.table_schema:
CREATE TABLE stakeholder_engagement (
  id INT PRIMARY KEY,
  stakeholder_name VARCHAR(255),
  interactive_map_url TEXT,
  report_url TEXT
);

CREATE TABLE stakeholders (
    stakeholder_id INT PRIMARY KEY,
    stakeholder_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20),
);

CREATE TABLE stakeholders (
	sid INT PRIMARY KEY,
	name VARCHAR(100),
	email VARCHAR(100)
);

CREATE TABLE interactive_maps (
	imid INT PRIMARY KEY,
	map_name VARCHAR(100),
	map_data JSONB
);

CREATE TABLE reports (
	repid INT PRIMARY KEY,
	report_name VARCHAR(100),
	report_data JSONB
);

CREATE TABLE community_projects (
	projid INT PRIMARY KEY,
	project_name VARCHAR(100),
	proj_description TEXT,
	due_date DATE
);

CREATE TABLE collaborator (
	id INT PRIMARY KEY,
	name VARCHAR(50) NOT NULL,
	email VARCHAR(100) NOT NULL
);

CREATE TABLE interactive_map (
	id INT PRIMARY KEY,
	map_name VARCHAR(100) NOT NULL,
	description TEXT
);

CREATE TABLE report (
	id INT PRIMARY KEY,
	report_name VARCHAR(100) NOT NULL,
	date_created DATE
);

CREATE TABLE community_engagement (
	id INT PRIMARY KEY,
	event_name VARCHAR(100) NOT NULL,
	event_date DATE
);
